<script>
    const emailInput = document.getElementById("recipient-email");
    const securityQuestionAnswer = document.getElementById("security-question-answer");
    const autoDepositMessage = document.getElementById("auto-deposit-message");

    emailInput.addEventListener("input", function() {
        const email = this.value;

        if (email !== "") {
            securityQuestionAnswer.style.display = "none";
            autoDepositMessage.style.display = "block";
        } else {
            securityQuestionAnswer.style.display = "block";
            autoDepositMessage.style.display = "none";
        }
    });
</script>



